import React, { Component } from 'react';
import HomeScreen from './app/containers/Home/HomeScreen';
import Sort from './app/containers/Home/Sort';
import CardItem from './app/containers/Home/CardItem';
class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            valueP: '',
        }
    };
    callBackHandle = (valueStatus) => {
        this.setState({
            valueP: valueStatus,
        })
    }

    render() {
        // const a = 'Coomber'
        console.log(this.state.valueP)
        return(
            <div className="container-fluid">
                <HomeScreen />
                <Sort callBackHandle={this.callBackHandle}/>
                <CardItem filterValue={this.state.valueP}/>
            </div>
            );
    }
}

export default HomePage;
