import React, { Component } from 'react';
import Items from '../../components/Items';
import Filter from './Filter';
import data from './Items.json';
class CardItem extends Component {
    constructor(props) {
        super(props)
        this.state = {
            search:null
        };
    }
    filterTarget=(e)=>{
            let keyword = e.target.value;
            this.setState({search:keyword}) 
        }
    render() {
        const items = data.filter((data)=>{
            if(this.state.search == null)
                return data
            else if(data.name.toLowerCase().includes(this.state.search.toLowerCase()) || data.category.toLowerCase().includes(this.state.search.toLowerCase())){
                return data
            }
        }).map(data =>{
            return(
                <Items 
                    name={data.name}
                    cagetory={data.cagetory}
                    price={data.price}
                    image={data.image}
                    rating={data.rating}
                >
                    {data.name}
                </Items>
            )
        })
        // let element = data.map((data,name) => (
        //     <Items 
        //                 key={name}
        //                 name={data.name}
        //                 cagetory={data.cagetory}
        //                 price={data.price}
        //                 image={data.image}
        //                 rating={data.rating}
        //             >
        //                 {data.name}
        //     </Items>
        // ))
        return (
            <div className="list-item">
                    <div className="row">
                        <Filter />
                        {items}
                        {/* {element} */}
                    </div>
            </div>
        )
}
}
export default CardItem;
