import React, { Component } from 'react';
import RangeSlide from '../../components/RangeSlide';
import DropDown from '../../components/DropDown';
import data from '../../components/Dropdown.json';
class Filter extends Component {
    constructor(props) {
        super(props)
        this.state ={
            filter: [
                'collection',
                'color',
                'category'
            ]
        }
    };

    filterValue = (filter) => {
        if(filter.collecttion === "Collection")
        {
            this.props.filter({collection:filter.collection})
        }
        else if ( filter.color === "Color")
        {
            this.props.filter({color:filter.color})
        }
        else if ( filter.category === "Category")
        {
            this.props.filter({category:filter.category})
        }
        console.log({filter});
        };
    render(){
        let element = data.map((data,index) => (
            <DropDown 
                key={index}
                select={data.select}
                option={data.option}
                filter={this.filterValue}
            >
            </DropDown>
        )        
        )
        return (
            <div className="list-group col-sm-3">
            <p style={{mixBlendMode: 'normal',opacity: 0.5,textTransform: 'uppercase'}}>  Filter By</p>
            <br/>
            {element} 
            <RangeSlide />          
            </div>
        );
    }
}

export default Filter;
