import React, { Component } from 'react';
import RangeSlide from '../../components/RangeSlide';
import DropDown from '../../components/DropDown';
import data from '../../components/Dropdown.json';
class Filter extends Component {
    constructor(props) {
        super(props)
        this.state = {
        filterCollection: '',
        filterColor: '',
        filterCategory: ''
        }
    };    
    render() {
        const { filterCollection,filterColor,filterCategory } = this.state;
        // console.log(data.select)
        let element = data.map((data,index) => (
            <DropDown 
                key={index}
                select={data.select}
                option={data.option}
            >
            </DropDown>
        )        
        )
        if(data.select === 'Collection')
            {
                this.setState({filterCollection:data.select})
            }
        else if (data.select === 'Color')
            {
                this.setState({filterColor:data.select})
            }
        else if (data.select === 'Category') 
            {
                this.setState({filterCategory:data.select})
            }
        return (
            <div className="list-group col-sm-3">
            <p style={{mixBlendMode: 'normal',opacity: 0.5,textTransform: 'uppercase'}}>  Filter By</p>
            <br/>
            {element}
            <RangeSlide />          
            </div>
        );
    }
}

export default Filter;
