    constructor(props) {
        super(props)
        // conditon: {name};
        this.state = {
            valueP: '',
            search: null
        }
    };
    filterTarget(){
        const a = this.props.filterValue;
        this.setState({search:a})
    }
    callBackHandle = (valueStatus) => {
        this.setState({
            valueP: valueStatus,
        })
    }

    render() {
        // const a = 'Coomber'
        console.log(this.state.valueP)
