import React, { Component } from 'react';
import HomeScreen from './app/containers/Home/HomeScreen';
import Sort from './app/containers/Home/Sort';
// import CardItem from './app/containers/Home/CardItem';
import Items from './app/components/Items';
import data from './app/containers/Home/Items.json';
import Filter from './app/containers/Home/Filter';
class HomePage extends Component {
    constructor(props) {
        super(props)
        // conditon: {name};
        this.state = {
            valueP: '',
            search:null,
        }
    };
    callBackHandle = (valueStatus) => {
        this.setState({
            valueP:valueStatus
        })
    }
    filterTarget = (value) => {
        // const a = this.state.valueP;
        this.setState({search:value})
    }

    render() {
        const items = data.filter((data)=>{
            if(this.state.search == null)
                return data
            else if(data.name.toLowerCase().includes(this.state.search.toLowerCase()) || data.cagetory.toLowerCase().includes(this.state.search.toLowerCase())){
                return data
            }
        }).map(data =>{
            return(
                <Items 
                    key={data.name}
                    name={data.name}
                    cagetory={data.cagetory}
                    price={data.price}
                    image={data.image}
                    rating={data.rating}
                >
                    {data.name}
                </Items>
            )
        })
        console.log(this.state.valueP)
        return(
            <div className="container-fluid">
                <HomeScreen />
                <Sort
                // searchValue={this.callBackHandle}
                changeValue={this.filterTarget}                
                />
                {/* <CardItem filterValue={this.state.valueP} /> */}
                <div className="list-item">
                    <div className="row">
                        <Filter />
                        {items}
                    </div>
                    <button onClick={()=>this.filterTarget()}> click me! </button> 
                </div>
            </div>
            );
    }
}

export default HomePage;
